mapper.select:
SELECT TD.ID
,TD.CONTENTS
,TD.STATUS_TYPE
,TD.REG_DTS
,TD.MOD_DTS
FROM TODO TD
WHERE TD.ID = #{id}

  AND TD.CONTENTS LIKE #{contents}||'%'


  AND TD.STATUS_TYPE = #{statusType}


  AND TD.MOD_DTS between #{modDtsAfter} and #{modDtsBefore}


  AND TD.REG_DTS between #{regDtsAfter} and #{regDtsBefore}

SELECT COUNT(*)
FROM TODO TD
WHERE TD.ID = #{id}

SELECT TD.ID
,TD.CONTENTS
,TD.STATUS_TYPE
,TD.REG_DTS
,TD.MOD_DTS
FROM TODO TD
WHERE 1=1

  AND TD.ID IN

    #{item}



  AND TD.CONTENTS LIKE #{contents}||'%'


  AND TD.STATUS_TYPE = #{statusType}


  AND TD.MOD_DTS between #{modDtsAfter} and #{modDtsBefore}


  AND TD.REG_DTS between #{regDtsAfter} and #{regDtsBefore}

LIMIT #{limit} OFFSET #{offset};

SELECT COUNT(*)
FROM REF_TODO
WHERE ID = #{refId}
  AND REF_ID = #{id}

mapper.update:
UPDATE TODO
SET CONTENTS = #{contents}

  ,STATUS_TYPE = #{statusType}

WHERE ID = #{id}
